"use strict";var form=document.querySelector(".form"),inputs=form.querySelectorAll(".required"),emailInput=document.querySelector(".form input[name=email]"),phoneInput=document.querySelector(".form input[name=phone]"),passwordInput=document.querySelector(".form input[name=password]"),repeatInput=document.querySelector(".form input[name=repeat]");function handleSubmit(e){e.preventDefault(),resetErrorMessage(),requiredInput(),validationEmail(),validationPhone(),repeatPassword()}form.addEventListener("submit",handleSubmit);var resetErrorMessage=function(){form.querySelectorAll(".error").forEach(function(e){return e.remove()})},createErrorMessage=function(e,r){var t=document.createElement("p");t.className="error",t.textContent=r,e.after(t)},requiredInput=function(){return inputs.forEach(function(e){if(!e.value){createErrorMessage(e,"Это поле обязательно к заполнению")}})},validationEmail=function(){if(!/^([a-z0-9_\.-]+)@([a-z0-9_\.-]+)\.([a-z\.]{2,6})$/.test(emailInput.value)){createErrorMessage(emailInput,"Введите валидный email")}},validationPhone=function(){if(!/^([+]{0,1})([0-9]{10,12})$/.test(phoneInput.value)){createErrorMessage(phoneInput,"Введите реальный номер телефона")}},repeatPassword=function(){if(passwordInput.value!==repeatInput.value){createErrorMessage(repeatInput,"Пароли должны совпадать")}};